function repmat(a,b){var c,d=[];for(c=0;b>c;c++)d=d.concat(a);return d}function range(a){var b,c=[];for(b=0;a>b;b++)c[b]=b+1;return c}function meshgrid(a){var b,c=[],d=[];for(b=0;a>b;b++)c[b]=range(a),d[b]=repmat(b+1,a);return{x:c,y:d}}function renderBoard(a,b){var c,d,e=document.getElementById("mycanvas"),f=a.length,g=e.height/f,h=e.getContext("2d");for(c=0;f>c;c++)for(d=0;f>d;d++)a[c][d]<b?h.fillStyle="#fff":h.fillStyle="#000",h.fillRect(g*d,g*c,g,g)}function munch(){var a,b,c=[],d=meshgrid(N);for(a=0;a<N;a++)for(c[a]=repmat(0,N),b=0;b<N;b++)c[a][b]=d.x[a][b]^d.y[a][b];var e=range(N);e=e.concat(range(N-1).reverse()),e.pop();var f=0;setInterval(function(){renderBoard(c,e[f]),f===e.length-1?f=0:f++},step)}